import React, { useState, useRef, useEffect } from "react";

import dummySool from "../static/dummyData";
import ProductCard from "./ProductCard";
import './ProductSlide.css'

const ProductSlide = () => {
    // 카드 + margin 값 -> 220px
    const cardSize = 220;

    // product-slide__wrapper의 width를 가져오기 위해 useRef 사용한다.
    const ref = useRef()

    // 페이지 당 들어갈 수 있는 카드 수는 product-slide__wrapper의 width 값에 따라 달라져야 한다. -> setLimit으로 관리
    // 유동적으로 관리할 수 있다면! -> ProductSlide.css 안의  flex-wrap 속성 설정해야 함
    // 페이지 당 들어갈 카드 수 limit (state)
    const [limit, setLimit] = useState(3);

    // 페이지 바뀔 때마다 리렌더링
    // 현재 페이지 번호 page (state)
    const [page, setPage] = useState(1);

    const getSlideSize = () => {
        // 현재 슬라이드 전체의 크기 가져오기
        let newWidth = ref.current.clientWidth;

        if(newWidth > cardSize * 5){
            setLimit(5)
        }else if(newWidth > cardSize * 4){
            setLimit(4)
        }else if(newWidth > cardSize * 3){
            setLimit(3)
        }else if(newWidth > cardSize * 2){
            setLimit(2)
        }else{
            setLimit(1)
        }

    }

    // 윈도우 크기가 바뀔 때마다 slide의 size 가져오기 
    useEffect(() => {
        window.addEventListener("resize", getSlideSize);
    }, []);


    // 현재 페이지의 첫번째 요소의 index 구하기
    // ex) 2페이지 면 리스트 중 3번째 요소이다. (2 - 1) * 3
    const offset = (page - 1) * limit; 

    // 전체 페이지 계산 : 전체 데이터의 개수를 페이지 당 표시할 개수로 나눈다.
    const numPage = Math.ceil(dummySool.length / limit)

    return (
        <div className="product-slide">
            {/* 이전 페이지 이동 */}
            <button onClick={() => setPage(page-1)} disabled={page === 1}>left</button>
            <ul className="product-slide__wrapper" ref={ref}>
                {/* 페이지 별로 slice 해서 보여준다 */}
                {dummySool.slice(offset, offset+limit).map((sool) => <li key={sool.id}><ProductCard sool={sool}/></li>)}
            </ul>
            {/* 다음 페이지 이동 */}
            <button onClick={() => setPage(page + 1)} disabled={page === numPage}>right</button>
        </div>
    )
}

export default ProductSlide;
