import React, { useState, useRef, useEffect } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import "./ProductSwiper.css"
import dummySool from "../static/dummyData";
import ProductCard from "./ProductCard";

// import required modules
import { Pagination, Navigation } from "swiper";


function getWindowSize() {
    const {innerWidth : width, innerHeight: height} = window;
    return {width, height};
}

export default function ProductSwiper() {

    const cardSize = 220;
    const [slide, setSlide] = useState(3);
    const [widowSize, setWindowSize] = useState(getWindowSize())

    useEffect(() => {

        const handleResize = () => {
            const newWidth = slideRef.current.clientWidth;

            if(newWidth <= cardSize + 10) setSlide(1);
            else if(newWidth <= cardSize * 2 + 10) setSlide(2);
            else setSlide(3);
        }

        handleResize();
        window.addEventListener("resize", handleResize)

        return () => { // 언마운트
            window.removeEventListener("resize", handleResize)
        }

    }, [])

    return (
        <div className="swiper-container" ref = {slideRef}>
            <Swiper
                slidesPerView={slide}
                spaceBetween={10}
                slidesPerGroup={slide}
                loop={true}
                loopFillGroupWithBlank={true}
                pagination={{
                    clickable: true,
                }}
                navigation={true}
                modules={[Pagination, Navigation]}
                className="mySwiper"
            >
                {dummySool.map((sool) => <SwiperSlide><ProductCard sool={sool}/></SwiperSlide>)}
            </Swiper>
        </div>
  );
}
