import React, { useState, useRef, useEffect } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import "./ProductSwiper.css"
import dummySool from "../static/dummyData";
import ProductCard from "./ProductCard";

// import required modules
import { Pagination, Navigation } from "swiper";

export default function ProductSwiper() {

    const slideRef = useRef()
    const cardSize = 220;
    const [slide, setSlide] = useState();

    // const changeWidth = () => {
    //     const newWidth = slideRef.current.clientWidth;

    //     if(newWidth <= cardSize + 1) setLimit(1);
    //     else if(newWidth <= cardSize * 2 + 1) setLimit(2);
    //     else setLimit(3);
    // }

    // useEffect(() => {
    //     changeWidth();
    //     window.addEventListener("resize", changeWidth)
    // }, [])

    return (
        <>
            <Swiper
                slidesPerView={3}
                spaceBetween={10}
                slidesPerGroup={3}
                loop={true}
                loopFillGroupWithBlank={true}
                pagination={{
                    clickable: true,
                }}
                navigation={true}
                modules={[Pagination, Navigation]}
                className="mySwiper"
                ref={slideRef}
            >
                {dummySool.map((sool) => <SwiperSlide><ProductCard key={sool.id} sool={sool}/></SwiperSlide>)}
            </Swiper>
        </>
  );
}
