import React, { useState } from "react";

import dummySool from "../static/dummyData";
import ProductCard from "./ProductCard";
import './ProductSlide.css'

const ProductSlide = () => {
    // 카드 + margin 값 -> 220px
    const slidePage = 220;

    // 페이지 당 들어갈 수 있는 카드 수는 product-slide__item의 width 값에 따라 달라져야 한다.
    // 페이지 당 들어갈 카드 수 limit (state)
    const [limit, setLimit] = useState(3);

    // 현재 페이지 번호
    const [page, setPage] = useState(1);

    // 현재 페이지의 첫번째 요소의 index 구하기
    // ex) 2페이지 면 리스트 중 3번째 요소이다. (2 - 1) * 3
    const offset = (page - 1) * limit; 

    // 

    return (
        <div className="product-slide">
            <button>left</button>
            <ul className="product-slide__item">
                {/* 페이지 별로 slice 해서 보여준다 */}
                {dummySool.slice(offset, offset+limit).map((sool) => <li><ProductCard key={sool.id} sool={sool}/></li>)}
            </ul>
            <button>right</button>
        </div>
    )
}

export default ProductSlide;
