import React, { useState, useRef, useEffect } from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import "./ProductSwiper.css"
import dummySool from "../static/dummyData";
import ProductCard from "./ProductCard";

// import required modules
import { Pagination, Navigation } from "swiper";

export default function ProductSwiper() {

    const slideRef = useRef()
    const cardSize = 220;
    const [slide, setSlide] = useState(1);

    const changeWidth = () => {
        const newWidth = slideRef.current.clientWidth;

        if(newWidth <= cardSize + 1) setSlide(1);
        else if(newWidth <= cardSize * 2 + 1) setSlide(2);
        else setSlide(3);
    }

    useEffect(() => {
        changeWidth();
        window.addEventListener("resize", changeWidth)
    }, [])

    return (
        <div className="swiper-container" ref = {slideRef}>
            <Swiper
                slidesPerView={slide}
                spaceBetween={10}
                slidesPerGroup={slide}
                loop={true}
                loopFillGroupWithBlank={true}
                pagination={{
                    clickable: true,
                }}
                navigation={true}
                modules={[Pagination, Navigation]}
                className="mySwiper"
            >
                {dummySool.map((sool) => <SwiperSlide><ProductCard sool={sool}/></SwiperSlide>)}
            </Swiper>
        </div>
  );
}
